function saveAbaultGoal() {
  const itemId = document.getElementById('abaultGoalItem').value;
  const days = parseInt(document.getElementById('abaultGoalDays').value);
  
  if (!itemId) {
    showToast('❌ Selecione um item!');
    return;
  }
  
  if (!days || days < 1) {
    showToast('❌ Defina uma meta de dias!');
    return;
  }
  
  // Verifica se já existe meta para esse item
  const existingIndex = abaultGoals.findIndex(g => g.itemId === itemId);
  if (existingIndex !== -1) {
    abaultGoals.splice(existingIndex, 1);
  }
  
  const item = ABAULT_DEFAULT_ITEMS.find(i => i.id === itemId);
  const data = abaultData[itemId] || { history: [] };
  
  abaultGoals.push({
    id: Date.now(),
    itemId: itemId,
    targetDays: days,
    startDate: new Date().toISOString(),
    startStreak: data.history && data.history.length > 0 
      ? getDaysSinceAbault(data.history[0].date) 
      : 0
  });
  
  saveAbaultGoals();
  closeAbaultGoalModal();
  renderAbaultGoals();
  
  const name = item.isCustom ? (data.customName || 'Item') : item.name;
  showToast(`🎯 Meta criada: ${days} dias sem ${name}!`);
}

function renderAbaultGoals() {
  const container = document.getElementById('abaultGoals');
  if (!container) return;
  
  if (abaultGoals.length === 0) {
    container.innerHTML = `
      <div class="abault-goals-empty">
        <div style="font-size: 24px; margin-bottom: 8px;">🎯</div>
        <div>Nenhuma meta ativa</div>
        <div style="font-size: 11px; color: var(--text-muted); margin-top: 4px;">
          Crie uma meta para se manter motivado!
        </div>
      </div>
    `;
    return;
  }
  
  let html = '';
  
  abaultGoals.forEach(goal => {
    const item = ABAULT_DEFAULT_ITEMS.find(i => i.id === goal.itemId);
    const data = abaultData[goal.itemId] || { history: [] };
    const name = item.isCustom ? (data.customName || 'Item') : item.name;
    
    let currentDays = 0;
    if (data.history && data.history.length > 0) {
      currentDays = getDaysSinceAbault(data.history[0].date);
    }
    
    const progress = Math.min((currentDays / goal.targetDays) * 100, 100);
    const isComplete = currentDays >= goal.targetDays;
    const isFailed = data.history && data.history.length > 0 && 
      new Date(data.history[0].date) > new Date(goal.startDate);
    
    let statusClass = '';
    let statusIcon = '🔄';
    if (isComplete) {
      statusClass = 'complete';
      statusIcon = '🏆';
    } else if (isFailed) {
      statusClass = 'failed';
      statusIcon = '❌';
    } else if (progress >= 75) {
      statusClass = 'almost';
      statusIcon = '🔥';
    }
    
    html += `
      <div class="abault-goal-card ${statusClass}">
        <div class="abault-goal-header">
          <span class="abault-goal-icon">${item.icon}</span>
          <div class="abault-goal-info">
            <div class="abault-goal-name">${name}</div>
            <div class="abault-goal-target">${goal.targetDays} dias sem consumir</div>
          </div>
          <span class="abault-goal-status">${statusIcon}</span>
          <button class="abault-goal-delete" onclick="deleteAbaultGoal(${goal.id})">×</button>
        </div>
        
        <div class="abault-goal-progress">
          <div class="abault-goal-progress-bar">
            <div class="abault-goal-progress-fill" style="width: ${progress}%"></div>
          </div>
          <div class="abault-goal-progress-text">
            <span>${currentDays} / ${goal.targetDays} dias</span>
            <span>${Math.round(progress)}%</span>
          </div>
        </div>
        
        ${isComplete ? `
          <div class="abault-goal-complete-msg">
            🎉 Parabéns! Meta alcançada!
            <button onclick="shareAbaultGoalComplete('${goal.itemId}', ${goal.targetDays})" class="abault-share-goal-btn">📤 Compartilhar</button>
          </div>
        ` : ''}
      </div>
    `;
  });
  
  container.innerHTML = html;
}

function deleteAbaultGoal(goalId) {
  if (!confirm('Remover esta meta?')) return;
  
  abaultGoals = abaultGoals.filter(g => g.id !== goalId);
  saveAbaultGoals();
  renderAbaultGoals();
  showToast('🗑️ Meta removida');
}

function shareAbaultGoalComplete(itemId, targetDays) {
  const item = ABAULT_DEFAULT_ITEMS.find(i => i.id === itemId);
  const data = abaultData[itemId] || { history: [] };
  const name = item.isCustom ? (data.customName || 'Item') : item.name;
  
  let text = `🏆 META ALCANÇADA!\n\n`;
  text += `${item.icon} ${name}\n`;
  text += `✅ ${targetDays} dias sem consumir!\n\n`;
  text += `💪 Consistência é a chave do sucesso!\n`;
  text += `📱 Acompanhando com Exercise Tracker`;
  
  if (navigator.share) {
    navigator.share({ title: 'Meta Alcançada!', text: text }).catch(() => {});
  } else {
    copyToClipboard(text);
  }
}

// ==================== ABAULT - RELATÓRIO SEMANAL ====================

function renderAbaultWeeklyReport() {
  const container = document.getElementById('abaultWeeklyReport');
  if (!container) return;
  
  const now = new Date();
  const weekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
  const twoWeeksAgo = new Date(now.getTime() - 14 * 24 * 60 * 60 * 1000);
  
  let thisWeekCount = 0;
  let lastWeekCount = 0;
  let itemsThisWeek = {};
  
  ABAULT_DEFAULT_ITEMS.forEach(item => {
    if (item.isCustom) return;
    
    const data = abaultData[item.id] || { history: [] };
    
    (data.history || []).forEach(entry => {
      const entryDate = new Date(entry.date);
      
      if (entryDate >= weekAgo) {
        thisWeekCount++;
        itemsThisWeek[item.id] = (itemsThisWeek[item.id] || 0) + 1;
      } else if (entryDate >= twoWeeksAgo && entryDate < weekAgo) {
        lastWeekCount++;
      }
    });
  });
  
  const diff = thisWeekCount - lastWeekCount;
  const isImproved = diff < 0;
  const isSame = diff === 0;
  
  // Itens mais consumidos esta semana
  const topItems = Object.entries(itemsThisWeek)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 3);
  
  let html = `
    <div class="abault-weekly-stats">
      <div class="abault-weekly-stat">
        <div class="abault-weekly-value">${thisWeekCount}</div>
        <div class="abault-weekly-label">Esta semana</div>
      </div>
      <div class="abault-weekly-stat">
        <div class="abault-weekly-value">${lastWeekCount}</div>
        <div class="abault-weekly-label">Semana passada</div>
      </div>
      <div class="abault-weekly-stat ${isImproved ? 'good' : (isSame ? '' : 'bad')}">
        <div class="abault-weekly-value">${isImproved ? '↓' : (isSame ? '→' : '↑')} ${Math.abs(diff)}</div>
        <div class="abault-weekly-label">${isImproved ? 'Melhorou!' : (isSame ? 'Igual' : 'Piorou')}</div>
      </div>
    </div>
  `;
  
  if (topItems.length > 0) {
    html += `
      <div class="abault-weekly-top">
        <div class="abault-weekly-top-title">📈 Mais consumidos esta semana:</div>
        <div class="abault-weekly-top-list">
    `;
    
    topItems.forEach(([itemId, count], index) => {
      const item = ABAULT_DEFAULT_ITEMS.find(i => i.id === itemId);
      if (item) {
        html += `
          <div class="abault-weekly-top-item">
            <span class="rank">#${index + 1}</span>
            <span class="icon">${item.icon}</span>
            <span class="name">${item.name}</span>
            <span class="count">${count}x</span>
          </div>
        `;
      }
    });
    
    html += '</div></div>';
  } else {
    html += `
      <div class="abault-weekly-clean">
        🎉 Semana limpa! Nenhum registro.
      </div>
    `;
  }
  
  container.innerHTML = html;
}

