    updateStatBar('rpgVitality', 'rpgVitalityBar', attrs.vitality);
    updateStatBar('rpgDiscipline', 'rpgDisciplineBar', attrs.discipline);
    updateStatBar('rpgPower', 'rpgPowerBar', attrs.power);
